mapper.insert:
insert into role(
                      name,
                      status,
                      remark
)
value (


    #{name},
    #{status},
    #{remark}
)

mapper.update:
update role set name=#{name},
                  status=#{status},
                  remark=#{remark}
             where id=#{id}

mapper.select:
select * from role
where 1=1

    and age = #{age}
