mapper.insert:
insert into userrole(
                      name,
                      status,
                      remark
)
value (
    #{name},
    #{status},
    #{remark}
)

mapper.update:
update userrole set name=#{name},
                  status=#{status},
                  remark=#{remark}
             where id=#{id}

mapper.select:
select * from userrole
where 1=1

    and age = #{age}
